
'use strict';(function(){var navMain=document.querySelector('.main-nav__list');var navToggle=document.querySelector('.main-nav__toggle');var header=document.querySelector('.header');header.classList.remove('header--nojs');navMain.classList.remove('main-nav__list--nojs');navToggle.classList.remove('main-nav__toggle--nojs');navToggle.addEventListener('click',function(){if(navMain.classList.contains('main-nav__list--closed')){navMain.classList.remove('main-nav__list--closed');navMain.classList.add('main-nav--opened');navToggle.classList.remove('main-nav__toggle--closed');navToggle.classList.add('main-nav__toggle--opened');}else{navMain.classList.add('main-nav__list--closed');navMain.classList.remove('main-nav__list--opened');navToggle.classList.remove('main-nav__toggle--opened');navToggle.classList.add('main-nav__toggle--closed');}});})();